import React,{useEffect,useState} from "react";


const search_api="https://api.themoviedb.org/3/search/movie?&api_key=7d12b72621ac9b497306428405cfb59d";
function App(){
    const [search,setSearch] = useState(' ');
  useEffect(()=>{
    fetch(search_api+search) 
    .then((res)=>res.json())
      .then((data) =>{ 
        console.log(data);
        setSearch(data.results);
    });
  },[])
  const handleOnSubmit= (e) => {
      e.preventDefault();

      fetch(search_api+search) 
      .then((res)=>res.json())
        .then((data) =>{ 
          console.log(data);
          setSearch(data.results);
        });
  }
  const handleOnChange = (e) =>{
      setSearch(e.target.value)

  }

    return (

        <form onSubmit={handleOnSubmit}>
        <div className="movie_container">
            <h1 className="title" >Movie Search </h1>
        <form onSubmit={handleOnSubmit}>
         <input
            className="search"
            type="search"
            placeholder="Search"
            value={search} onChange={handleOnChange}></input>



          </form>
        </div>
        </form>
    );
    }
export default App;
