import React from 'react';
import { useNavigate } from 'react-router-dom';

export default function Login(props) {
    let navigate = useNavigate();
    const handleForm = (e) => {
        e.preventDefault();
        let username = e.target.username.value;
        let password = e.target.password.value;

        let myHeaders = new Headers();
        myHeaders.append("Authorization", "Basic " + btoa(`${username}:${password}`));

        fetch('https://kekambas-blog.herokuapp.com/auth/token', {
            method: 'POST',
            headers: myHeaders
        }).then(res => res.json())
            .then(data => {
                props.logIn(data['token']);
                props.flashMessage('You have successfully logged in', 'success');
                navigate('/');
            })
    }
    return (
        <form onSubmit={handleSubmit}>
            <h3 className='text-center'>Register Here</h3>
            <div className='form-group'>
                <fieldset>
                    <label htmlFor='username'>Username</label>
                    <input type='text' name='username' className='form-control' placeholder='Username' />
                </fieldset>
                <fieldset>
                    <label htmlFor='email'>Email</label>
                    <input type='email' name='email' className='form-control' placeholder='Email' />
                </fieldset>
                <fieldset>
                    <label htmlFor='password'>Password</label>
                    <input type='password' name='password' className='form-control' placeholder='Password' />
                </fieldset>
                <fieldset>
                    <label htmlFor='confirmPass'>Confirm Password</label>
                    <input type='password' name='confirmPass' className='form-control' placeholder='Confirm Password' />
                </fieldset>
                <input type='submit' className='btn btn-outline-primary w-100 mt-3' value='Register' />
            </div>
        </form>
    );
}
