import React from 'react';
import { useNavigate } from 'react-router-dom';

export default function Register(props) {
    let navigate = useNavigate();

    const handleSubmit= (e) => {
        e.preventDefault();
        let username = e.target.username.value;
        let email = e.target.email.value;
        let password = e.target.password.value;
        let confirmPass = e.target.confirmPass.value;

        if (password !== confirmPass) {
            props.flashMessage('Passwords do not match, please try again.', 'danger');
            navigate('/register');
        }
        let myHeaders = new Headers();
        myHeaders.append('Content-Type', 'application/json')
        let data = JSON.stringify({
            "username": username,
            "email": email,
            "password": password
        })

        fetch('https://kekambas-blog.herokuapp.com/auth/users', {
            method: 'POST',
            headers: myHeaders,
            body: data
        }).then(res => res.json())
            .then(data => {
                console.log(data);
                props.flashMessage('Thank you for registering!', 'success');
                navigate('/');
            })
        }
    return (
        <form onSubmit={handleSubmit}>
            <h3 className='text-center'>Register Here</h3>
            <div className='form-group'>
                <fieldset>
                    <label htmlFor='username'>Username</label>
                    <input type='text' name='username' className='form-control' placeholder='Username' />
                </fieldset>
                <fieldset>
                    <label htmlFor='email'>Email</label>
                    <input type='email' name='email' className='form-control' placeholder='Email' />
                </fieldset>
                <fieldset>
                    <label htmlFor='password'>Password</label>
                    <input type='password' name='password' className='form-control' placeholder='Password' />
                </fieldset>
                <fieldset>
                    <label htmlFor='confirmPass'>Confirm Password</label>
                    <input type='password' name='confirmPass' className='form-control' placeholder='Confirm Password' />
                </fieldset>
                <input type='submit' className='btn btn-outline-primary w-100 mt-3' value='Register' />
            </div>
        </form>

    );
}
