import React,{useEffect,useState} from "react";


const search_api="https://api.themoviedb.org/3/search/movie?&api_key=7d12b72621ac9b497306428405cfb59d";
function App(){
    useEffect(()=>{
    const [search,setSearch] = useState(' ');

    fetch(search_api+search) 
    .then((res)=>res.json())
      .then((data) =>{ 
        console.log(data);
        setSearch(data.results);
    });
  },[])
  const handleOnSubmit= (e) => {
      e.preventDefault();

      fetch(search_api+search) 
      .then((res)=>res.json())
        .then((data) =>{ 
          console.log(data);
          setSearch(data.results);
        });
  }
  const handleOnChange = (e) =>{
      setSearch(e.target.value)

  }

    return (
        <>
        <body>
            <form onSubmit={handleOnSubmit}>
            <input className="search" type="search"
            placeholder="Search"
            value={search}
            onChange={handleOnChange}/>
            </form>
        </body>



       </> 
    );
    }
export default App;
